mapper.select:
select p.ID_, p.NAME_, p.KEY_, p.VERSION_, p.DEPLOYMENT_ID_, p.RESOURCE_NAME_, p.DGRM_RESOURCE_NAME_, p.HAS_START_FORM_KEY_, p.HAS_GRAPHICAL_NOTATION_, p.SUSPENSION_STATE_, d.DEPLOY_TIME_ from p left join d on p.DEPLOYMENT_ID_ = d.ID_ where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and d.DEPLOY_TIME_ >= #{pd.lastStart}
and d.DEPLOY_TIME_ <= #{pd.lastEnd}
order by d.DEPLOY_TIME_ desc
